import React, { ChangeEvent } from "react";
import { User, fetchUsers } from '../api/api'; // Import the API function and User interface
import '../css/style.css'; // Ensure to import your CSS file
import { debounce } from 'lodash';

interface State {
    users: User[];
    currentPage: number;
    pageSize: number;
    searchTerm: string;
}

export class Example extends React.Component<{}, State> {
    state: State = {
        users: [],
        currentPage: 1,
        pageSize: 30,
        searchTerm: ""
    };

    componentDidMount() {
        document.title = "Example - My React Application";
        this.loadUsers();
    }

    loadUsers = debounce(() => {
        const { currentPage, pageSize, searchTerm } = this.state;
        fetchUsers(currentPage, pageSize, searchTerm)
            .then(apiUsers => {
                this.setState({ users: apiUsers });
            })
            .catch(error => {
                console.error("Failed to load users", error);
            });
    }, 300); // 300ms debounce delay

    handlePrevious = () => {
        this.setState(
            prevState => ({ currentPage: Math.max(prevState.currentPage - 1, 1) }),
            this.loadUsers
        );
    };

    handleNext = () => {
        this.setState(
            prevState => ({ currentPage: prevState.currentPage + 1 }),
            this.loadUsers
        );
    };

    handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
        this.setState({ searchTerm: e.target.value }, () => {
            this.loadUsers();
        });
    };

    formatDate = (dateString: string) => {
        const options: Intl.DateTimeFormatOptions = { year: 'numeric', month: '2-digit', day: '2-digit' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    render(): JSX.Element {
        const { users, currentPage, searchTerm } = this.state;

        return (
            <div className="example-container">
                <div className="search-container mb-3">
                    <div className="search-wrapper">
                        <input
                            type="text"
                            className="form-control search-input"
                            value={searchTerm}
                            onChange={this.handleSearchChange}
                            placeholder="Find a book"
                        />
                    </div>
                </div>
                <div className="table-container">
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>First Name</th>
                                <th>Last Name</th>
                                <th>Email</th>
                                <th>Date Created</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(user => (
                                <tr key={user.id}>
                                    <td>{user.first_name}</td>
                                    <td>{user.last_name}</td>
                                    <td>{user.email}</td>
                                    <td>{this.formatDate(user.date_created)}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <div className="pagination-buttons">
                    <button
                        className="btn btn-info"
                        type="button"
                        onClick={this.handlePrevious}
                        disabled={currentPage === 1}
                    >
                        Previous
                    </button>
                    <button
                        className="btn btn-info"
                        type="button"
                        onClick={this.handleNext}
                    >
                        Next
                    </button>
                </div>
            </div>
        );
    }
}
