import React, { ChangeEvent, FormEvent } from "react";
import '../css/style.css';

interface State {
    firstName: string;
    lastName: string;
    email: string;
    errors: {
        firstName?: string;
        lastName?: string;
        email?: string;
    };
}

export class Exam extends React.Component<{}, State> {
    state: State = {
        firstName: "",
        lastName: "",
        email: "",
        errors: {}
    };

    // Set the document title when the component mounts
    componentDidMount() {
        document.title = "Exam - My React Application";
    }

    // Validate form inputs
    validate = (): boolean => {
        const { firstName, lastName, email } = this.state;
        const errors: State["errors"] = {};
        let isValid = true;

        const nameRegex = /^[A-Za-z\s]+$/; // Regex to check for valid names

        // Validate first name
        if (!firstName.trim()) {
            errors.firstName = "First name is required";
            isValid = false;
        } else if (!nameRegex.test(firstName)) {
            errors.firstName = "First name cannot contain numbers or special characters";
            isValid = false;
        }

        // Validate last name
        if (!lastName.trim()) {
            errors.lastName = "Last name is required";
            isValid = false;
        } else if (!nameRegex.test(lastName)) {
            errors.lastName = "Last name cannot contain numbers or special characters";
            isValid = false;
        }

        // Validate email
        if (!email.trim()) {
            errors.email = "Email is required";
            isValid = false;
        } else if (!/\S+@\S+\.\S+/.test(email)) {
            errors.email = "Email is invalid";
            isValid = false;
        }

        // Update the state with validation errors
        this.setState({ errors });
        return isValid;
    };

    // Handle input changes
    handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        this.setState(prevState => ({
            ...prevState,
            [name]: value
        } as Pick<State, keyof State>));
    };

    // Handle form submission
    handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        if (this.validate()) {
            const { firstName, lastName, email } = this.state;
            const user = {
                first_name: firstName,
                last_name: lastName,
                email: email
            };

            try {
                // Send a POST request to the backend to add a new user
                const response = await fetch('http://localhost:5225/users', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(user)
                });

                if (response.ok) {
                    // Handle success
                    alert("User added successfully!");
                    // Reset the form fields and errors
                    this.setState({ firstName: "", lastName: "", email: "", errors: {} });
                } else {
                    // Handle error
                    alert("Failed to add user. Please try again.");
                }
            } catch (error) {
                console.error("Error submitting form:", error);
                alert("An error occurred. Please try again.");
            }
        }
    };

    render() {
        const { firstName, lastName, email, errors } = this.state;

        return (
            <div className="form-container">
                <h2>User Details</h2>
                <form className="project-form" onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="firstName">First Name</label>
                        <input
                            type="text"
                            id="firstName"
                            name="firstName"
                            value={firstName}
                            onChange={this.handleChange}
                            placeholder="First Name"
                        />
                        {errors.firstName && <div className="error">{errors.firstName}</div>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="lastName">Last Name</label>
                        <input
                            type="text"
                            id="lastName"
                            name="lastName"
                            value={lastName}
                            onChange={this.handleChange}
                            placeholder="Last Name"
                        />
                        {errors.lastName && <div className="error">{errors.lastName}</div>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={email}
                            onChange={this.handleChange}
                            placeholder="Email"
                        />
                        {errors.email && <div className="error">{errors.email}</div>}
                    </div>
                    <div className="form-group button-group d-flex justify-content-center gap-2 mt-3">
                        <button className="btn btn-info btn-lg" type="submit">Submit</button>
                    </div>
                </form>
            </div>
        );
    }
}
