import React from "react";
import { User } from "../shared/User";

interface State {
    users: User[];
    currentPage: number;
    pageSize: number;
}

export class Example extends React.Component<{}, State> {
    state: State = {
        users: [],
        currentPage: 1,
        pageSize: 30
    };

    componentDidMount() {
        document.title = "Example - My React Application";
        this.loadUsers();
    }

    loadUsers = () => {
        const { currentPage, pageSize } = this.state;
        fetch(`http://localhost:5225/users/?PageNumber=${currentPage}&PageSize=${pageSize}`)
            .then(response => response.json())
            .then(apiUsers => {
                this.setState({ users: apiUsers });
            });
    };

    handlePrevious = () => {
        this.setState(
            prevState => ({ currentPage: Math.max(prevState.currentPage - 1, 1) }),
            this.loadUsers
        );
    };

    handleNext = () => {
        this.setState(
            prevState => ({ currentPage: prevState.currentPage + 1 }),
            this.loadUsers
        );
    };

    formatDate = (dateString: string) => {
        const options: Intl.DateTimeFormatOptions = { year: 'numeric', month: '2-digit', day: '2-digit' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    render(): JSX.Element {
        const { users, currentPage } = this.state;

        return (
            <div>
                <h2>Example</h2>
                <div className="row">
                    <div className="col-lg-12">
                        <div className="tb-container">
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Id</th>
                                        <th>First Name</th>
                                        <th>Last Name </th>
                                        <th>Email</th>
                                        <th>Date Created</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {users.map(user => (
                                        <tr key={user.id}>
                                            <td>{user.id}</td>
                                            <td>{user.first_name}</td>
                                            <td>{user.last_name}</td>
                                            <td>{user.email}</td>
                                            <td>{this.formatDate(user.date_created)}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                        <div className="d-flex justify-content-center gap-2 mt-3">
                            <button
                                className="btn btn-info btn-lg"
                                type="button"
                                onClick={this.handlePrevious}
                                disabled={currentPage === 1}
                            >
                                Previous
                            </button>
                            <button
                                className="btn btn-info btn-lg"
                                type="button"
                                onClick={this.handleNext}
                            >
                                Next
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
