import React, { ChangeEvent, FormEvent } from "react";
import '../css/style.css';

interface State {
    firstName: string;
    lastName: string;
    email: string;
    errors: {
        firstName?: string;
        lastName?: string;
        email?: string;
    };
}

export class Exam extends React.Component<{}, State> {
    state: State = {
        firstName: "",
        lastName: "",
        email: "",
        errors: {}
    };

    componentDidMount() {
        document.title = "Exam - My React Application";
    }

    validate = (): boolean => {
        const { firstName, lastName, email } = this.state;
        const errors: State["errors"] = {};
        let isValid = true;

        const nameRegex = /^[A-Za-z\s]+$/; // Regex to check for alphabets and spaces only

        if (!firstName.trim()) {
            errors.firstName = "First name is required";
            isValid = false;
        } else if (!nameRegex.test(firstName)) {
            errors.firstName = "First name cannot contain numbers or special characters";
            isValid = false;
        }

        if (!lastName.trim()) {
            errors.lastName = "Last name is required";
            isValid = false;
        } else if (!nameRegex.test(lastName)) {
            errors.lastName = "Last name cannot contain numbers or special characters";
            isValid = false;
        }

        if (!email.trim()) {
            errors.email = "Email is required";
            isValid = false;
        } else if (!/\S+@\S+\.\S+/.test(email)) {
            errors.email = "Email is invalid";
            isValid = false;
        }

        this.setState({ errors });
        return isValid;
    };

    handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        this.setState(prevState => ({
            ...prevState,
            [name]: value
        } as Pick<State, keyof State>));
    };

    handleSubmit = (e: FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        if (this.validate()) {
            // Perform submission logic here
            console.log("Form submitted successfully!");
        }
    };

    render() {
        const { firstName, lastName, email, errors } = this.state;

        return (
            <div className="form-container">
                <h2>User Details</h2>
                <form className="project-form" onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="firstName">First Name</label>
                        <input
                            type="text"
                            id="firstName"
                            name="firstName"
                            value={firstName}
                            onChange={this.handleChange}
                            placeholder="First Name"
                        />
                        {errors.firstName && <div className="error">{errors.firstName}</div>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="lastName">Last Name</label>
                        <input
                            type="text"
                            id="lastName"
                            name="lastName"
                            value={lastName}
                            onChange={this.handleChange}
                            placeholder="Last Name"
                        />
                        {errors.lastName && <div className="error">{errors.lastName}</div>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={email}
                            onChange={this.handleChange}
                            placeholder="Email"
                        />
                        {errors.email && <div className="error">{errors.email}</div>}
                    </div>
                    <div className="form-group button-group">
                        <button className="btn btn-info" type="submit">Submit</button>
                    </div>
                </form>
            </div>
        );
    }
}
