import React, { ChangeEvent } from "react";
import { User, fetchUsers } from '../api'; // Import the API function and User interface

interface State {
    users: User[];
    currentPage: number;
    pageSize: number;
    searchTerm: string;
}

export class Example extends React.Component<{}, State> {
    state: State = {
        users: [],
        currentPage: 1,
        pageSize: 30,
        searchTerm: ""
    };

    componentDidMount() {
        document.title = "Example - My React Application";
        this.loadUsers();
    }

    loadUsers = () => {
        const { currentPage, pageSize, searchTerm } = this.state;
        fetchUsers(currentPage, pageSize, searchTerm)
            .then(apiUsers => {
                this.setState({ users: apiUsers });
            })
            .catch(error => {
                console.error("Failed to load users", error);
            });
    };

    handlePrevious = () => {
        this.setState(
            prevState => ({ currentPage: Math.max(prevState.currentPage - 1, 1) }),
            this.loadUsers
        );
    };

    handleNext = () => {
        this.setState(
            prevState => ({ currentPage: prevState.currentPage + 1 }),
            this.loadUsers
        );
    };

    handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
        this.setState({ searchTerm: e.target.value });
    };

    handleSearch = () => {
        this.loadUsers();
    };

    formatDate = (dateString: string) => {
        const options: Intl.DateTimeFormatOptions = { year: 'numeric', month: '2-digit', day: '2-digit' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    render(): JSX.Element {
        const { users, currentPage, searchTerm } = this.state;

        return (
            <div>
                <h2>Example</h2>
                <div className="row mb-3">
                    <div className="col-lg-12 d-flex justify-content-center">
                        <input
                            type="text"
                            className="form-control"
                            value={searchTerm}
                            onChange={this.handleSearchChange}
                            placeholder="Search by email"
                            style={{ borderRadius: "20px", width: "300px" }}
                        />
                        <button
                            className="btn btn-info ml-2"
                            type="button"
                            onClick={this.handleSearch}
                            style={{ borderRadius: "20px" }}
                        >
                            Search
                        </button>
                    </div>
                </div>
                <div className="row">
                    <div className="col-lg-12">
                        <div className="tb-container">
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>First Name</th>
                                        <th>Last Name</th>
                                        <th>Email</th>
                                        <th>Date Created</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {users.map(user => (
                                        <tr key={user.id}>
                                            <td>{user.first_name}</td>
                                            <td>{user.last_name}</td>
                                            <td>{user.email}</td>
                                            <td>{this.formatDate(user.date_created)}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                        <div className="d-flex justify-content-center gap-2 mt-3">
                            <button
                                className="btn btn-info btn-lg"
                                type="button"
                                onClick={this.handlePrevious}
                                disabled={currentPage === 1}
                            >
                                Previous
                            </button>
                            <button
                                className="btn btn-info btn-lg"
                                type="button"
                                onClick={this.handleNext}
                            >
                                Next
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
