import React from "react";
import '../css/style.css'; 

export class Exam extends React.Component {
    state = {
        firstName: '',
        lastName: '',
        email: '',
        errors: {
            firstName: '',
            lastName: '',
            email: '',
        }
    };

    componentDidMount() {
        document.title = "Exam - My React Application";
    }

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value }, () => { this.validateField(name, value) });
    };

    validateField = (name, value) => {
        let errors = this.state.errors;

        switch (name) {
            case 'firstName':
            case 'lastName':
                errors[name] = /^[a-zA-Z]+$/.test(value) ? '' : 'Name cannot contain numbers or special characters';
                break;
            case 'email':
                errors.email = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value) ? '' : 'Email is not valid';
                break;
            default:
                break;
        }

        this.setState({ errors });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        const { firstName, lastName, email, errors } = this.state;
        if (firstName && lastName && email && !errors.firstName && !errors.lastName && !errors.email) {
            alert('Form submitted successfully');
        } else {
            alert('Please fix the errors in the form');
        }
    };

    render() {
        return (
            <div className="form-container">
                <h2>User Details</h2>
                <form className="project-form" onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="firstName">First Name</label>
                        <input 
                            type="text" 
                            id="firstName" 
                            name="firstName" 
                            placeholder="First Name" 
                            value={this.state.firstName}
                            onChange={this.handleChange}
                        />
                        {this.state.errors.firstName && <span className="error">{this.state.errors.firstName}</span>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="lastName">Last Name</label>
                        <input 
                            type="text" 
                            id="lastName" 
                            name="lastName" 
                            placeholder="Last Name" 
                            value={this.state.lastName}
                            onChange={this.handleChange}
                        />
                        {this.state.errors.lastName && <span className="error">{this.state.errors.lastName}</span>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input 
                            type="email" 
                            id="email" 
                            name="email" 
                            placeholder="Email" 
                            value={this.state.email}
                            onChange={this.handleChange}
                        />
                        {this.state.errors.email && <span className="error">{this.state.errors.email}</span>}
                    </div>
                    <div className="form-group button-group">
                        <button className="btn btn-info" type="submit">Submit</button>
                    </div>
                </form>
            </div>
        );
    }
}
