import React, { ChangeEvent } from "react";
import { User, fetchUsers } from '../api/api'; // Import the API function and User interface
import './App.scss';

interface State {
    users: User[];
    currentPage: number;
    pageSize: number;
    searchTerm: string;
    hasMoreUsers: boolean; // New state to track if more users are available
}

export class Example extends React.Component<{}, State> {
    private debounceTimeout: NodeJS.Timeout | null = null;

    constructor(props: {}) {
        super(props);
        this.state = {
            users: [],
            currentPage: 1,
            pageSize: 30,
            searchTerm: "",
            hasMoreUsers: true, // Initialize with true
        };
    }

    componentDidMount() {
        document.title = "Example - My React Application";
        this.loadUsers();
    }

    componentDidUpdate(prevProps: {}, prevState: State) {
        if (prevState.currentPage !== this.state.currentPage || prevState.pageSize !== this.state.pageSize || prevState.searchTerm !== this.state.searchTerm) {
            this.loadUsers();
        }
    }

    loadUsers = () => {
        const { currentPage, pageSize, searchTerm } = this.state;
        fetchUsers(currentPage, pageSize, searchTerm)
            .then(apiUsers => {
                this.setState({
                    users: apiUsers,
                    hasMoreUsers: apiUsers.length === pageSize, // Check if the number of users fetched is equal to pageSize
                });
            })
            .catch(error => {
                console.error("Failed to load users", error);
            });
    };

    handlePrevious = () => {
        this.setState(
            prevState => ({ currentPage: Math.max(prevState.currentPage - 1, 1) }),
            this.loadUsers
        );
    };

    handleNext = () => {
        this.setState(
            prevState => ({ currentPage: prevState.currentPage + 1 }),
            this.loadUsers
        );
    };

    handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
        const searchTerm = e.target.value;
        this.setState({ searchTerm, currentPage: 1 }); // Reset currentPage to 1

        if (this.debounceTimeout) {
            clearTimeout(this.debounceTimeout);
        }

        this.debounceTimeout = setTimeout(() => {
            this.loadUsers();
        }, 300); // 300ms debounce delay
    };

    formatDate = (dateString: string) => {
        const options: Intl.DateTimeFormatOptions = { year: 'numeric', month: '2-digit', day: '2-digit' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    render() {
        const { users, currentPage, searchTerm, hasMoreUsers } = this.state;

        return (
            <div className="example-container">
                <div className="search-container mb-3">
                    <div className="search-wrapper">
                        <input
                            type="text"
                            className="form-control search-input"
                            value={searchTerm}
                            onChange={this.handleSearchChange}
                            placeholder="Find a User"
                        />
                    </div>
                </div>
                <div className="table-container">
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>First Name</th>
                                <th>Last Name</th>
                                <th>Email</th>
                                <th>Date Created</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(user => (
                                <tr key={user.id}>
                                    <td>{user.first_name}</td>
                                    <td>{user.last_name}</td>
                                    <td>{user.email}</td>
                                    <td>{this.formatDate(user.date_created)}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <div className="pagination-buttons">
                    <button
                        className="btn btn-info"
                        type="button"
                        onClick={this.handlePrevious}
                        disabled={currentPage === 1}
                    >
                        Previous
                    </button>
                    <button
                        className="btn btn-info"
                        type="button"
                        onClick={this.handleNext}
                        disabled={!hasMoreUsers} // Disable if there are no more users
                    >
                        Next
                    </button>
                </div>
            </div>
        );
    }
}

export default Example;
