import React, { useState, useEffect, ChangeEvent } from "react";
import { User, fetchUsers } from '../api/api'; // Import the API function and User interface
import '../css/style.css'; // Ensure to import your CSS file

const Example: React.FC = () => {
    const [users, setUsers] = useState<User[]>([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSize] = useState(30);
    const [searchTerm, setSearchTerm] = useState("");

    useEffect(() => {
        document.title = "Example - My React Application";
        loadUsers();
    }, [currentPage, pageSize]);

    useEffect(() => {
        const handler = setTimeout(() => {
            loadUsers();
        }, 300); // 300ms debounce delay

        return () => {
            clearTimeout(handler);
        };
    }, [searchTerm]);

    const loadUsers = () => {
        fetchUsers(currentPage, pageSize, searchTerm)
            .then(apiUsers => {
                setUsers(apiUsers);
            })
            .catch(error => {
                console.error("Failed to load users", error);
            });
    };

    const handlePrevious = () => {
        setCurrentPage(prevPage => Math.max(prevPage - 1, 1));
    };

    const handleNext = () => {
        setCurrentPage(prevPage => prevPage + 1);
    };

    const handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
        setSearchTerm(e.target.value);
    };

    const formatDate = (dateString: string) => {
        const options: Intl.DateTimeFormatOptions = { year: 'numeric', month: '2-digit', day: '2-digit' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    return (
        <div className="example-container">
            <div className="search-container mb-3">
                <div className="search-wrapper">
                    <input
                        type="text"
                        className="form-control search-input"
                        value={searchTerm}
                        onChange={handleSearchChange}
                        placeholder="Find a book"
                    />
                </div>
            </div>
            <div className="table-container">
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Email</th>
                            <th>Date Created</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td>{user.first_name}</td>
                                <td>{user.last_name}</td>
                                <td>{user.email}</td>
                                <td>{formatDate(user.date_created)}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="pagination-buttons">
                <button
                    className="btn btn-info"
                    type="button"
                    onClick={handlePrevious}
                    disabled={currentPage === 1}
                >
                    Previous
                </button>
                <button
                    className="btn btn-info"
                    type="button"
                    onClick={handleNext}
                >
                    Next
                </button>
            </div>
        </div>
    );
};

export default Example;
